import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import {Link} from "react-router-dom";
import './ChatGPT.css';

export default function ChatGPT() {
  const [response, setResponse] = useState("");
  const location = useLocation();
  const title = new URLSearchParams(location.search).get("title");
  const [updatedDescription, setUpdatedDescription] = useState("");
  const HTTP = "http://localhost:8080/chat";

  useEffect(() => {
    // Request response using description as the initial prompt when the component mounts
    if (title) {
      axios
        .post(HTTP, { prompt: title })
        .then((res) => {
          setResponse(res.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [title, HTTP]);
  const handleDescriptionUpdate = () => {
    // Update description in the database
    axios
      .post("http://localhost:3004/update", {
        title: title,
        description: updatedDescription
      })
      .then((res) => {
        console.log(res.data);
        // Optionally, you can redirect the user to another page or refresh the current page
      })
      .catch((error) => {
        console.log(error);
      });
  };



  return (
    <div className="bigcon">
      <div className="container container-sm p-1">
        <h1 className="title text-center text-darkGreen">Details of meeting</h1>
        <h2 >This is a details of the meeting</h2>
        <div className="bg-darkGreen mt-2 p-1 border-5">
          <p className="text-light">
            {response ? response : "Response is loading..."}
          </p>
        </div>
        <br />
        <br />
        <Link to="/">
            <button type="submit">Confirm meeting details</button>
        </Link>
      </div>
    </div>
  );
}
