import React, { useState, useEffect } from 'react';
import './meetingStatus.css';
import Axios from "axios";

function MeetingStatus() {
  const [MeetingList, setMeetingList] = useState([]);

  useEffect(() => {
    const getMeeting = () => {
      Axios.get("http://localhost:3004/Meeting")
        .then((response) => {
          setMeetingList(response.data);
        })
        .catch((error) => {
          console.error("Error fetching meeting data:", error);
        });
    };

    getMeeting(); // Call the function when the component mounts

  }, []); // Empty dependency array means this effect runs only once on mount

  return (
    <form>
      <div className="statusTopic">
        <center><h1>Meeting Status</h1></center>
        <h2>Topic : Weekly checkup 4</h2>
      </div>
      <div className='mainContent'>
        {MeetingList.map((val, key) => (
          <div key={key} className="card-body text-left">
            <p className="card-text">Title: {val.title}</p>
            <p className="card-text">Date: {val.date}</p>
            <p className="card-text">Description: {val.description}</p>
          </div>
        ))}
      </div>
    </form>
  );
}

export default MeetingStatus;
