import React from "react";
import './meetingSetup.css';
import { useState } from "react";
import Axios from "axios";
function MeetingSetup() {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState(0);
  const [description,setDescription] = useState("");
  const [MeetingList, setMeetingList] = useState([]);
  const getMeeting = () => {
    Axios.get("http://localhost:3004/Employee").then((response) => {
        setMeetingList(response.data);
    });
  };

  const addMeeting = () => {
    Axios.post("http://localhost:3004/create", {
      title: title,
      date: date,
    }).then(() => {
      setMeetingList([
        ...setMeetingList,
        {
            title: title,
            date: date,
        },
      ]);
    });
  };
    return (
      <div className="meetingsetup">
        <h1>Meeting Setup</h1>
        <div className="contentsetup">
        <label for="title">Title:</label>
        <input type="text" id="title" name="title" placeholder="Enter meeting title" 
        onChange={(event) => {
            setTitle(event.target.value)
          }}
        />
        <br />
        <br />
        <label for="date">Date:</label>
        <input type="date" id="date" name="date" 
         onChange={(event) => {
            setDate(event.target.value)
          }}
        />
        <br />
        <br />
        <label for="description">Title:</label>
        <input type="text" id="description" name="description" placeholder="Enter meeting description" 
        onChange={(event) => {
            setDescription(event.target.value)
          }}
        />
        <br />
        <br />
        <button type="submit" onClick={addMeeting}>Confirm</button>
        </div>
            <div className="contentmeeting">
            <button class="btn btn-primary" onClick={getMeeting}>
                Show Meeting
            </button>
        </div>
            </div>
    );
  }
export default MeetingSetup;
