import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import './ChatGPT.css';

export default function ChatGPT() {
  const [response, setResponse] = useState("");
  const [gptResponse, setGptResponse] = useState(""); // State to hold GPT response
  const location = useLocation();
  const title = new URLSearchParams(location.search).get("title");
  const [updatedDescription, setUpdatedDescription] = useState("");
  const HTTP = "http://localhost:8080/chat";

  useEffect(() => {
    // Request response using description as the initial prompt when the component mounts
    if (title) {
      axios
        .post(HTTP, { prompt: title })
        .then((res) => {
          setResponse(res.data);
          // Set GPT response to the input field
          setGptResponse(res.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [title, HTTP]);

  const handleDescriptionUpdate = (title) => {
    // Update description in the database
    axios
      .put("http://localhost:3004/update", {
        title: title,
        description: gptResponse,
      })
      .then((res) => {
        // Update the state with the new description
        setUpdatedDescription(gptResponse);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div className="bigcon">
      <div className="container container-sm p-1">
        <h1 className="title text-center text-darkGreen">Details of meeting</h1>
        <h2 >This is a details of the meeting {title}</h2>
        <label >GPT Prompt : </label>
        <input className="updateInput"
          type="text"
          id="title"
          name="title"
          value={title} // Set the value of the input to the GPT response
          onChange={(event) => {
            setUpdatedDescription(event.target.value)
          }} />
        <h2 >GPT Response</h2>
        <div className="bg-darkGreen mt-2 p-1 border-5">
          <p className="text-light">
            {response ? response : "Response is loading..."}
          </p>
        </div>
        <div className="inputbox">
        <br /> 
        <label>Updated Description:</label>
        <br />
        <br />
        <input className="updateInput"
          type="text"
          id="description"
          name="description"
          value={gptResponse} // Set the value of the input to the GPT response
          onChange={(event) => {
            setUpdatedDescription(event.target.value)
          }}
        />
        <br />
        <Link to={`/systemprocess?title=${encodeURIComponent(title)}`}>
          <button onClick={() => { handleDescriptionUpdate(title) }}>Update Description</button>
        </Link>
        </div>
        <br />
        <br />
        <Link to={`/systemprocess?title=${encodeURIComponent(title)}`}>
          <button type="submit">Confirm meeting details</button>
        </Link>
      </div>
    </div>
  );
}
